import { useState, useRef, useEffect, useMemo, useCallback } from 'react';
import './App.css';
import emailjs from '@emailjs/browser';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
const configJson = require('./portfolioContentConfig.json');

const { projectListArr, iconLinkList } = configJson;

const watchOptions = {
  root: null,
  rootMargin: '0px',
  threshold: 0.6,
};

function App() {
  const [errors, setErrors] = useState({});
  const [sectionNavElements, setSectionNavElements] = useState();
  const [sectionElements, setSectionElements] = useState();
  const [techStackIcon] = useState(iconLinkList);
  const [projectList] = useState(projectListArr);
  const formRef = useRef();

  const SERVICE_ID = 'service_q1dfvyf';
  const TEMPLATE_ID = 'template_ekuvbwm';
  const PUBLIC_KEY = 'IGn9HDE3zYGPnsn79';

  useEffect(() => {
    setSectionElements(document.querySelectorAll('section'));
    setSectionNavElements(document.getElementsByClassName('sectionNav')[0].getElementsByTagName('li'));
  }, []);

  const updateMenuOnScroll = useCallback((sectionIndex) => {
    for (let listEl of sectionNavElements) {
      listEl.firstChild.classList.remove('current');
    }
    sectionNavElements[sectionIndex].firstChild.classList.add('current');
  }, [sectionNavElements]);

  const sectionWatcherCallback = useCallback((sections) => {
    sections.forEach((section) => {
      if (!section.isIntersecting) return;
      updateMenuOnScroll(section.target.id);
    });
  }, [updateMenuOnScroll]);

  const sectionWatcher = useMemo(() => {
    return new IntersectionObserver(sectionWatcherCallback, watchOptions);
  }, [sectionWatcherCallback]);

  useEffect(() => {
    sectionElements?.forEach((section) => {
      sectionWatcher.observe(section);
    });

    return () => {
      sectionElements?.forEach((section) => {
        sectionWatcher.unobserve(section);
      });
    };
  }, [sectionElements, sectionWatcher]);

  const navigateToSection = (sectionIndex) => {
    if (sectionIndex === undefined) return;
    updateMenuOnScroll(sectionIndex);
    if (sectionIndex <= sectionElements.length - 1) {
      sectionElements[sectionIndex]?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const validateForm = () => {
    const form = formRef.current;
    const errors = {};
    const name = form.name.value.trim();
    const email = form.email.value.trim();
    const message = form.message.value.trim();

    if (!name) {
      errors.name = "Name is required";
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!email) {
      errors.email = "Email is required";
    } else if (!emailRegex.test(email)) {
      errors.email = "Invalid email address";
    }

    if (!message) {
      errors.message = "Message is required";
    }

    return errors;
  };

  const sendEmail = (e) => {
    e.preventDefault();
    const formErrors = validateForm();
    setErrors(formErrors);
    if (Object.keys(formErrors).length === 0) {
      emailjs
        .sendForm(SERVICE_ID, TEMPLATE_ID, formRef.current, {
          publicKey: PUBLIC_KEY,
        })
        .then(
          () => {
            showToast("success");
            setErrors({});
            formRef.current.reset();
          },
          () => {
            setErrors({});
            showToast("error");
          }
        );
    }
  };

  function showToast(toastType) {
    if (toastType === 'success') {
      toast.success('Email received', {
        position: "bottom-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
      });
    } else if (toastType === 'error') {
      toast.error('Error sending email', {
        position: "bottom-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
      });
    }
  }

  return (
    <main className="w-full h-screen overflow-y-scroll" style={{ scrollSnapType: 'y mandatory', scrollbarWidth: 'none' }}>
      {/* Section 1 */}
      <section id='0' className='w-full h-screen p-5' style={{ scrollSnapAlign: 'start' }}>
        <div className='bg-[#f4f9fc] h-full flex flex-col justify-center items-center'>
          <h1 className='text-5xl text-center text-blue-500 sm:text-6xl 2xl:text-8xl'><b>Hi, I am <br />Ambati Maneesha</b></h1>
          <ul className='absolute z-30 flex flex-col gap-2 2xl:gap-5 sectionNav right-7 bottom-9 sm:right-14 sm:bottom-11'>
            {Array.from({ length: 4 }).map((_, index) => (
              <li key={index}>
                <button className='w-3 h-3 transition-all rotate-45 border-2 border-blue-700 2xl:w-4 2xl:h-4' onClick={() => navigateToSection(index)}></button>
              </li>
            ))}
          </ul>
          <ul className='absolute left-0 z-30 flex flex-col items-center justify-center gap-4 p-4 bg-white rounded-md shadow-2xl opacity-75 sm:opacity-100 top-2 sm:top-auto shadow-cyan-900'>
            {['github', 'linkedin'].map(social => (
              <li key={social}>
                <a rel="noreferrer" target='_blank' href={techStackIcon[social]}>
                  <img alt={social} src={`https://cdn.jsdelivr.net/gh/devicons/devicon/icons/${social}/${social}-original.svg`} className='w-6 h-6 2xl:w-8 2xl:h-8' />
                </a>
              </li>
            ))}
            <li key='email'>
              <a href='mailto:ambatimaneesha552@gmail.com'>
                <img className="w-6 h-6 2xl:w-8 2xl:h-8" src="https://img.icons8.com/color/48/gmail--v1.png" alt="e-mail" />
              </a>
            </li>
          </ul>
          <a href="mailto:ambatimaneesha552@gmail.com?subject=Hi Maneesha, I would like to hire you." className='w-28 fixed 2xl:text-lg cursor-pointer text-center font-bold tracking-widest hover:tracking-[0.3em] transition-all text-blue-500 text-md top-11 right-4 sm:right-11 mix-blend-multiply'>HIRE ME</a>
          <div className='flex flex-row gap-6 text-lg mt-14 2xl:text-2xl 2xl:mt-16'>
            <button onClick={() => navigateToSection(2)} className='px-3 py-2 font-bold text-blue-500 transition-all border-2 border-blue-500 rounded-lg homeButton hover:bg-blue-500 hover:text-white hover:scale-x-110'>Projects</button>
            <a href={configJson.cv} target='_blank' rel="noreferrer">
              <button className='px-3 py-2 font-bold text-blue-500 transition-all border-2 border-blue-500 rounded-lg hover:bg-blue-500 hover:text-white hover:scale-x-110'>Get my CV</button>
            </a>
          </div>
        </div>
      </section>

      {/* Section 2 */}
      <section id='1' className='w-full h-screen bg-[#f4f9fc] flex sm:justify-center items-center' style={{ scrollSnapAlign: 'start' }}>
        <div className='flex text-sm md:text-lg lg:text-base flex-col lg:flex-row gap-6 px-4 lg:px-0 lg:max-w-[1000px] 2xl:text-xl 2xl:max-w-[1300px] 2xl:gap-8'>
          <article className='flex-1 lg:pr-8 text-justify text-[#0f1b61] lg:border-r-2 border-blue-500 2xl:pr-9'>
            <h1 className='mb-3 text-3xl font-bold text-blue-600 lg:text-4xl 2xl:mb-5 2xl:text-5xl'>About me</h1>
            <p className='indent-4'>Hello! I'm Ambati Maneesha, a passionate Full Stack Web Developer based in Pune, India. With a strong foundation in both front-end and back-end technologies, I specialize in creating dynamic, high-performance web applications.</p>
            <p className='indent-4'>I hold a <b>Bachelor of Engineering in Information Technology</b> from Army Institute of Technology.</p>
            <p className='indent-4'>I love coding, exploring new technologies, and am always looking for innovative ways to solve problems.</p>
          </article>
          <article className='flex-1 lg:pl-8 2xl:pl-9'>
            <h1 className='mb-3 text-3xl font-bold text-blue-600 lg:text-4xl 2xl:mb-5 2xl:text-5xl'>My Skills</h1>
            <ul className='flex flex-col gap-3 list-disc pl-5'>
              {['Java', 'Python', 'Django', 'React', 'Node.js', 'MongoDB', 'MySQL', 'HTML/CSS', 'Git', 'REST APIs'].map(skill => (
                <li key={skill} className='text-[#0f1b61] text-base 2xl:text-xl'>{skill}</li>
              ))}
            </ul>
          </article>
        </div>
      </section>

      {/* Section 3 */}
      <section id='2' className='w-full h-screen bg-[#f4f9fc] flex flex-col justify-center items-center' style={{ scrollSnapAlign: 'start' }}>
        <h1 className='text-5xl font-bold text-blue-600 mb-10 sm:text-6xl 2xl:text-8xl'>Projects</h1>
        <div className='flex flex-col gap-5 w-full max-w-[700px] sm:flex-row sm:flex-wrap sm:justify-center'>
          {projectList.map((project) => (
            <div key={project.id} className='bg-white rounded-lg shadow-lg transition-all hover:shadow-2xl p-5'>
              <h2 className='text-xl font-semibold text-blue-500'>{project.name}</h2>
              <p className='mt-2 text-gray-700'>{project.description}</p>
              <a href={project.link} target='_blank' rel='noreferrer'>
                <button className='mt-3 text-blue-500 underline'>View Project</button>
              </a>
            </div>
          ))}
        </div>
      </section>

      {/* Section 4 */}
      <section id='3' className='w-full h-screen bg-[#f4f9fc] flex flex-col items-center justify-center' style={{ scrollSnapAlign: 'start' }}>
        <h1 className='text-5xl font-bold text-blue-600 mb-10 sm:text-6xl 2xl:text-8xl'>Contact Me</h1>
        <form ref={formRef} onSubmit={sendEmail} className='w-full max-w-md p-6 bg-white rounded-lg shadow-lg'>
          <div className='mb-4'>
            <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='name'>Name</label>
            <input className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${errors.name ? 'border-red-500' : ''}`} id='name' name='name' type='text' placeholder='Your Name' />
            {errors.name && <p className='text-red-500 text-xs italic'>{errors.name}</p>}
          </div>
          <div className='mb-4'>
            <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='email'>Email</label>
            <input className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${errors.email ? 'border-red-500' : ''}`} id='email' name='email' type='email' placeholder='Your Email' />
            {errors.email && <p className='text-red-500 text-xs italic'>{errors.email}</p>}
          </div>
          <div className='mb-6'>
            <label className='block text-gray-700 text-sm font-bold mb-2' htmlFor='message'>Message</label>
            <textarea className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${errors.message ? 'border-red-500' : ''}`} id='message' name='message' placeholder='Your Message' />
            {errors.message && <p className='text-red-500 text-xs italic'>{errors.message}</p>}
          </div>
          <button type='submit' className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline'>Send Message</button>
        </form>
        <ToastContainer />
      </section>
    </main>
  );
}

export default App;
