import { useState, useRef, useEffect, useMemo, useCallback } from 'react';
import './App.css';
import emailjs from '@emailjs/browser';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
const configJson = require('./portfolioContentConfig.json');

const { projectListArr, iconLinkList } = configJson;
const watchOptions = {
  root: null,
  rootMargin: '0px',
  threshold: 0.6
}

function App() {
  const [errors, setErrors] = useState({});
  const [sectionNavElements, setSectionNavElements] = useState();
  const [sectionElements, setSectionElements] = useState();
  const [techStackIcon] = useState(iconLinkList);
  const [projectList] = useState(projectListArr);
  const formRef = useRef();

  const SERVICE_ID = 'service_q1dfvyf';
  const TEMPLATE_ID = 'template_ekuvbwm';
  const PUBLIC_KEY = 'IGn9HDE3zYGPnsn79';

  useEffect(() => {
    setSectionElements(document.querySelectorAll('section'));
    setSectionNavElements(document.getElementsByClassName('sectionNav')[0].getElementsByTagName('li'));
  }, [])

  const updateMenuOnScroll = useCallback((sectionIndex) => {
    for (let listEl of sectionNavElements) {
      listEl.firstChild.classList.remove('current');
    };
    sectionNavElements[sectionIndex].firstChild.classList.add('current');
  }, [sectionNavElements]);

  const sectionWatcherCallback = useCallback((sections) => {
    sections.forEach((section) => {
      if (!section.isIntersecting) return;
      updateMenuOnScroll(section.target.id);
    })
  }, [updateMenuOnScroll]);

  const sectionWatcher = useMemo(() => {
    return new IntersectionObserver(sectionWatcherCallback, watchOptions);
  }, [sectionWatcherCallback]);

  useEffect(() => {
    sectionElements?.forEach((section) => {
      sectionWatcher.observe(section);
    })

    return () => {
      sectionElements?.forEach((section) => {
        sectionWatcher.unobserve(section);
      })
    }
  }, [sectionElements, sectionWatcher]);

  function navigateToSection(sectionIndex) {
    if (sectionIndex === undefined) return;
    updateMenuOnScroll(sectionIndex);

    if (sectionIndex <= sectionElements.length - 1) {
      sectionElements[sectionIndex]?.scrollIntoView({ behavior: 'smooth' });
    }
  }

  const validateForm = () => {
    const form = formRef.current;
    const errors = {};
    const name = form.name.value.trim();
    const email = form.email.value.trim();
    const message = form.message.value.trim();

    if (!name) {
      errors.name = "Name is required";
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!email) {
      errors.email = "Email is required";
    } else if (!emailRegex.test(email)) {
      errors.email = "Invalid email address";
    }

    if (!message) {
      errors.message = "Message is required";
    }

    return errors;
  };

  const sendEmail = (e) => {
    e.preventDefault();
    const formErrors = validateForm();
    setErrors(formErrors);
    if (Object.keys(formErrors).length === 0) {
      emailjs
        .sendForm(SERVICE_ID, TEMPLATE_ID, formRef.current, {
          publicKey: PUBLIC_KEY,
        })
        .then(
          () => {
            showToast("success");
            setErrors({});
            formRef.current.reset();
          },
          (error) => {
            setErrors({});
            showToast("error");
          },
        );
    }
  };

  function showToast(toastType) {
    if (toastType === 'success') {
      toast.success('Email received', {
        position: "bottom-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
      });
    } else if (toastType === 'error') {
      toast.error('Error sending email', {
        position: "bottom-right",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
      });
    }
  }

  return (
    <main className="w-full h-screen overflow-y-scroll" style={{ 'scrollSnapType': 'y mandatory', 'scrollbarWidth': 'none' }}>
      <section id='0' className='w-full h-screen p-5' style={{ 'scrollSnapAlign': 'start' }}>
        <div className='bg-[#f4f9fc] h-full flex flex-col justify-center items-center'>
          <h1 className='text-5xl text-center text-blue-500 sm:text-6xl 2xl:text-8xl'><b>Hi, I am <br />Ambati Maneesha</b></h1>
          <ul className='absolute z-30 flex flex-col gap-2 2xl:gap-5 sectionNav right-7 bottom-9 sm:right-14 sm:bottom-11'>
            <li><button className='w-3 h-3 transition-all rotate-45 border-2 border-blue-700 2xl:w-4 2xl:h-4 current' onClick={e => { navigateToSection(0) }} data-sectionindex="0"></button></li>
            <li><button className='w-3 h-3 transition-all rotate-45 border-2 border-blue-700 2xl:w-4 2xl:h-4 ' onClick={e => { navigateToSection(1) }} data-sectionindex="1"></button></li>
            <li><button className='w-3 h-3 transition-all rotate-45 border-2 border-blue-700 2xl:w-4 2xl:h-4 ' onClick={e => { navigateToSection(2) }} data-sectionindex="2"></button></li>
            <li><button className='w-3 h-3 transition-all rotate-45 border-2 border-blue-700 2xl:w-4 2xl:h-4 ' onClick={e => { navigateToSection(3) }} data-sectionindex="3"></button></li>
          </ul>
          <ul className='absolute left-0 z-30 flex flex-col items-center justify-center gap-4 p-4 bg-white rounded-md shadow-2xl opacity-75 sm:opacity-100 top-2 sm:top-auto shadow-cyan-900'>
            {['github', 'linkedin'].map(social => 
              <li key={social}>
                <a rel="noreferrer" target='_blank' href={techStackIcon[social]}>
                  <img alt={social} src={`https://cdn.jsdelivr.net/gh/devicons/devicon/icons/${social}/${social}-original.svg`} className='w-6 h-6 2xl:w-8 2xl:h-8' />
                </a>
              </li>
            )}
            <li key='email'>
              <a href='mailto:ambatimaneesha552@gmail.com'>
                <img className="w-6 h-6 2xl:w-8 2xl:h-8" src="https://img.icons8.com/color/48/gmail--v1.png" alt="e-mail" />
              </a>
            </li>
          </ul>
          <a href="mailto:ambatimaneesha552@gmail.com?subject=Hi Maneesha, I would like to hire you." className='w-28 fixed 2xl:text-lg cursor-pointer text-center font-bold tracking-widest hover:tracking-[0.3em] transition-all text-blue-500 text-md top-11 right-4 sm:right-11 mix-blend-multiply'>HIRE ME</a>
          <div className='flex flex-row gap-6 text-lg mt-14 2xl:text-2xl 2xl:mt-16'>
            <button onClick={e => navigateToSection(2)} data-sectionindex="2" className='px-3 py-2 font-bold text-blue-500 transition-all border-2 border-blue-500 rounded-lg homeButton hover:bg-blue-500 hover:text-white hover:scale-x-110'>Projects</button>
            <a href={configJson.cv} target='_blank' rel="noreferrer">
              <button className='px-3 py-2 font-bold text-blue-500 transition-all border-2 border-blue-500 rounded-lg hover:bg-blue-500 hover:text-white hover:scale-x-110'>Get my CV</button>
            </a>
          </div>
        </div>
      </section>
      <section id='1' className='w-full h-screen bg-[#f4f9fc] flex sm:justify-center items-center' style={{ 'scrollSnapAlign': 'start' }}>
        <div className='flex text-sm md:text-lg lg:text-base flex-col lg:flex-row gap-6 px-4 lg:px-0 lg:max-w-[1000px] 2xl:text-xl 2xl:max-w-[1300px] 2xl:gap-8'>
          <div className='flex-1'>
            <h1 className='font-bold text-3xl text-blue-500 text-center'>About Me</h1>
            <p className='p-2'>I'm an enthusiastic developer with a passion for coding and learning new technologies. I enjoy solving problems and working on challenging projects.</p>
          </div>
          <div className='flex-1'>
            <h1 className='font-bold text-3xl text-blue-500 text-center'>Technologies</h1>
            <ul className='flex flex-col items-center'>
              {techStackIcon.map((icon, index) => (
                <li key={index} className='flex gap-2'>
                  <img alt='icon' src={icon.src} className='w-8 h-8 2xl:w-10 2xl:h-10' />
                  <span>{icon.name}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </section>
      <section id='2' className='w-full h-screen flex flex-col justify-center items-center bg-[#f4f9fc]' style={{ 'scrollSnapAlign': 'start' }}>
        <h1 className='font-bold text-3xl text-blue-500 text-center'>Projects</h1>
        <div className='grid grid-cols-1 gap-4 p-5 md:grid-cols-2 lg:grid-cols-3'>
          {projectList.map((project, index) => (
            <div key={index} className='shadow-lg rounded-md p-4 bg-white transition-all duration-300 hover:shadow-2xl'>
              <h2 className='font-bold text-lg'>{project.title}</h2>
              <p>{project.description}</p>
              <a href={project.link} className='text-blue-500'>View Project</a>
            </div>
          ))}
        </div>
      </section>
      <section id='3' className='w-full h-screen flex flex-col justify-center items-center bg-[#f4f9fc]' style={{ 'scrollSnapAlign': 'start' }}>
        <h1 className='font-bold text-3xl text-blue-500 text-center'>Contact Me</h1>
        <form ref={formRef} className='w-full max-w-md' onSubmit={sendEmail}>
          <div className='flex flex-col mb-4'>
            <label className='mb-2' htmlFor='name'>Name</label>
            <input className={`p-2 border rounded-md ${errors.name ? 'border-red-500' : 'border-gray-300'}`} type='text' name='name' id='name' />
            {errors.name && <span className='text-red-500'>{errors.name}</span>}
          </div>
          <div className='flex flex-col mb-4'>
            <label className='mb-2' htmlFor='email'>Email</label>
            <input className={`p-2 border rounded-md ${errors.email ? 'border-red-500' : 'border-gray-300'}`} type='email' name='email' id='email' />
            {errors.email && <span className='text-red-500'>{errors.email}</span>}
          </div>
          <div className='flex flex-col mb-4'>
            <label className='mb-2' htmlFor='message'>Message</label>
            <textarea className={`p-2 border rounded-md ${errors.message ? 'border-red-500' : 'border-gray-300'}`} name='message' id='message' rows='4'></textarea>
            {errors.message && <span className='text-red-500'>{errors.message}</span>}
          </div>
          <button type='submit' className='px-4 py-2 font-bold text-white bg-blue-500 rounded-md hover:bg-blue-700'>Send Message</button>
        </form>
      </section>
      <ToastContainer />
    </main>
  );
}

export default App;
